import axios from 'axios';
import { useEffect, useState } from 'react'

function useAjax(options) {

    const [data, setData] = useState({});

    useEffect(() => {
        async function ajaxCall() {

            try {
                const res = await axios(options);
                setData(res.data);
            } catch (error) {
                // setError(error);
            }
            return { data };
        }
    });
}

export default useAjax;











