import axios from 'axios';
import { useEffect, useState } from 'react'

async function useAjax () {

    const [ data, setData ] = useState({});

    try {
        const res = await axios(options);
        setData(res.data);
      } catch (error) {
        setError(error);
      }
      return {data};
    }     


export default useAjax;











