import axios from 'axios';
import { useEffect, useState } from 'react'

const useAjax = () => {

    const [data, setData] = useState({});
    const [options, request] = use
    useEffect((options) => {
        async function ajaxCall() {

            try {
                const res = await axios(options);
                setData(res.data);
            } catch (error) {
                // setError(error);
            }
        }
        ajaxCall();
    }, []);
    return { data };
}

export default useAjax;











